import React from 'react';
import 'animate.css/animate.min.css'; // Import animate.css
import useIntersectionObserver from './UseIntersectionObserver'; // Import the custom hook

const About = () => {
  const [introHeadingRef, isIntroHeadingVisible] = useIntersectionObserver({ threshold: 0.1 });
  const [introText1Ref, isIntroText1Visible] = useIntersectionObserver({ threshold: 0.1 });
  const [introText2Ref, isIntroText2Visible] = useIntersectionObserver({ threshold: 0.1 });
  const [introText3Ref, isIntroText3Visible] = useIntersectionObserver({ threshold: 0.1 });
  const [skillsHeadingRef, isSkillsHeadingVisible] = useIntersectionObserver({ threshold: 0.1 });
  const [skillsListRef, isSkillsListVisible] = useIntersectionObserver({ threshold: 0.1 });

  return (
    <div id='about' className="aboutContainer">
      <p className='aboutHeading'>About</p>
      <div className="innerContainer">
        <div className="aboutIntro">
          <p
            ref={introHeadingRef}
            className={`introHeading animate__animated ${isIntroHeadingVisible ? 'animate__slideInLeft animate__duration-2s' : ''}`}
          >
            Introduction
          </p>
          <p
            ref={introText1Ref}
            className={`introText animate__animated ${isIntroText1Visible ? 'animate__fadeInUpBig animate__duration-2s' : ''}`}
          >
            I'm a passionate MERN Stack developer, specializing in building and managing the front-end of modern
            web applications.
          </p>
          <br />
          <p
            ref={introText2Ref}
            className={`introText animate__animated ${isIntroText2Visible ? 'animate__slideInLeft animate__duration-2s' : ''}`}
          >
            I leverage the power of this JavaScript-based stack (MongoDB, Express, React, Node.js) to create user-centric
            interfaces that drive product success.
          </p>
          <br />
          <p
            ref={introText3Ref}
            className={`introText animate__animated ${isIntroText3Visible ? 'animate__slideInLeft animate__duration-2s' : ''}`}
          >
            Head over to the <span id='projects'>Projects</span> section to explore my work. Connect with me on LinkedIn.
            I'm always eager for new challenges and opportunities to grow my skills. If you have a project that aligns with my expertise, reach out!
            Let's collaborate and build something amazing together.
          </p>
        </div>

        <div className="aboutSkills">
          <p
            ref={skillsHeadingRef}
            className={`introHeading animate__animated ${isSkillsHeadingVisible ? 'animate__slideInRight animate__duration-2s' : ''}`}
          >
            Skills
          </p>
          <ul
            ref={skillsListRef}
            className={`skillsList animate__animated ${isSkillsListVisible ? 'animate__slideInRight animate__duration-2s' : ''}`}
          >
            <li>HTML</li>
            <li>CSS</li>
            <li>JavaScript</li>
            <li>React</li>
            <li>Node.js</li>
            <li>Express.js</li>
            <li>MongoDB</li>
            <li>Git</li>
            <li>Redux</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default About;
