import React from 'react';
import 'animate.css/animate.min.css'; // Import animate.css
import useIntersectionObserver from './UseIntersectionObserver'; // Import the custom hook

const Projects = () => {

  const projectRefs = projects.map(() => React.createRef());
  const visibilityStates = projectRefs.map(ref => useIntersectionObserver({ threshold: 0.1, ref }));

  const projects = [
    {
      title: 'Ride Reserve',
      side: 'left',
      img: 'eduspark.png',
      description: "React Js I Will Maintain Existing React Systems, Including Fixing Bugs, Design And Develop New Custom Components To Meet Project Requirements,Install And Configure Server Environments For React Deployments.",
      date: "12 July 2023"
    },
    {
      title: 'Edu Spark',
      side: 'right',
      img: 'eduspark.png',
      description: "React Js I Will Maintain Existing React Systems, Including Fixing Bugs, Design And Develop New Custom Components To Meet Project Requirements,Install And Configure Server Environments For React Deployments.",
      date: "12 July 2023"
    },
    {
      title: 'Pen Pulse',
      side: 'left',
      img: 'eduspark.png',
      description: "React Js I Will Maintain Existing React Systems, Including Fixing Bugs, Design And Develop New Custom Components To Meet Project Requirements,Install And Configure Server Environments For React Deployments.",
      date: "12 July 2023"
    },
  ];

 

  return (
    <div className="timeline-container">
      {projects.map((project, index) => {
        const isVisible = visibilityStates[index];
        const animationClass = project.side === 'left' ? 'animate__slideInLeft' : 'animate__slideInRight';

        return (
          <div
            key={index}
            ref={projectRefs[index]}
            className={`timeline-card ${project.side} animate__animated ${isVisible ? animationClass : 'hidden'}`}
          >
            <div className="timeline-content">
              <div className="card-front">
                <img src={project.img} alt={project.title} />
                <h3>{project.title}</h3>
              </div>
              <div className="card-back">
                <p>{project.description}</p>
                <span>{project.date}</span>
              </div>
            </div>
          </div>
        );
      })}
      <div className="timeline-line"></div>
    </div>
  );
};

export default Projects;
